          <ImagenProducto src={img} alt={nombre}/>
          <span>En stock: <span>{productoStock > 0? productoStock: "AGOTADO"}</span></span>
          <button disabled={productoStock === 0} property={span} onClick={() => {comprar();contador()}}>{productoStock === 0 ? "agotado" :"comprar"}</button>
    </div>
  )
}
